def generate_mail():
    idx = random.randint(6, 16)
    example = ['qq.com', '163.com', 'huohu.com', 'xinlang.com', '126.com']
    print(''.join(random.choice(string.ascii_lowercase) for k in range(idx)) + '@' + random.choice(example))
    print(''.join(random.choice(string.digits) for j in range(idx)) + '@' + random.choice(example))


